import {createStore} from 'vuex'
const store=createStore({
    state:{
        todos:[]
    },
    mutations:{
        setTodos(state,todos){
            state.todos=todos
        },
        addItem(state,todo){
            state.todos.push(todo)
        },
        removeTodos(state,index){
            state.todos.splice(index,1)
        }
    },

    getters:{
        todos:state=>state.todos
    }
})
export default store;
